mapper.select:
select * from reviewTbl where prodNum = #{prodNum}
	
		
			and subject like concat('%',#{keyWord},'%' )
		
		
			and regName like concat('%',#{keyWord},'%' )
		
		
			and content like concat('%',#{keyWord},'%' )
		
		
			
		
	
	order by tblReviewNum limit #{pageStart}, #{perPageNum}
